import React, { useState } from 'react';

const Tab = () => {
  const [currentTab, setCurrentTab] = useState('1');

//    this line of code sets up a state variable named currentTab with an initial value of '1', 
// and provides a method setCurrentTab to update this state.

  const tabs = [
    {
      id: 1,
      tabTitle: 'Tab 1',
      title: 'Title 1',
      content: 'this is the content for Tab 1'
    },
    {
      id: 2,
      tabTitle: 'Tab 2',
      title: 'Title 2',
      content: 'this is the content for Tab 2'
    },
    {
      id: 3,
      tabTitle: 'Tab 3',
      title: 'Title 3',
      content: 'this is the content for Tab 3'
    },
    {
      id: 4,
      tabTitle: 'Tab 4',
      title: 'Title 4',
      content: 'this is the content for Tab 4'
    }
  ];

  const handleTabClick = (tabId) => {
    setCurrentTab(tabId);
  };

  return (
    <div className='tabcontainer'>
      <div className='tabs'>
        {tabs.map((tab) => (
          <button
            key={tab.id}
            id={tab.id}
            disabled={currentTab === `${tab.id}`}
            onClick={() => handleTabClick(`${tab.id}`)}
          >
            {tab.tabTitle}
          </button>
        ))}
      </div>

      {/* //Provides a unique key for each button, which is required by React for efficiently rendering lists of elements.  */}

      <div className='content'>
        {tabs.map((tab) => (
          <div key={tab.id}>
            {currentTab === `${tab.id}` && (
              <div>
                <p className='title'>{tab.title}</p>
                <p>{tab.content}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tab;
